package hyper

import (
	"io/ioutil"
	"net/http"
	"reflect"
	"strings"
	"testing"
)

func TestExtractCommand(t *testing.T) {
	body := `{"@action":"foo", "bar": 3, "data":[{"type":"bar", "data":{}},{"type":"baz", "data":{}}]}`
	req, _ := http.NewRequest("POST", "/", strings.NewReader(body))
	c := ExtractCommand(req)

	if c.Action != "foo" {
		t.Errorf("want: %s, got: %s", "foo", c.Action)
	}
	if b := c.Arguments.Int("bar"); b != 3 {
		t.Errorf("want: %d, got: %d", 3, b)
	}

	type generic struct {
		Type string      `json:"type"`
		Data interface{} `json:"data"`
	}

	data := []generic{}
	err := c.Arguments.JSON("data", &data)
	if err != nil {
		t.Errorf("expected no error: %v", err)
	}
	want := []generic{
		{Type: "bar", Data: map[string]interface{}{}},
		{Type: "baz", Data: map[string]interface{}{}},
	}
	if !reflect.DeepEqual(want, data) {
		t.Errorf("\nwant: %#v\ngot: %#v\n", want, data)
	}
}

func TestCommand(t *testing.T) {

	c := MakeCommand()
	c.Arguments["foo"] = "data:text/plain;base64,SGVsbG8sIFdvcmxkIQ%3D%3D"
	c.Arguments["bar"] = "data:image/gif;base64,R0lGODlhEAAQAMQAAORHHOVSKudfOulrSOp3WOyDZu6QdvCchPGolfO0o/XBs/fNwfjZ0frl3/zy7////wAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAkAABAALAAAAAAQABAAAAVVICSOZGlCQAosJ6mu7fiyZeKqNKToQGDsM8hBADgUXoGAiqhSvp5QAnQKGIgUhwFUYLCVDFCrKUE1lBavAViFIDlTImbKC5Gm2hB0SlBCBMQiB0UjIQA7"
	c.Arguments["baz"] = "data:image/png;base64,"
	want := "Hello, World!"
	bs := c.Arguments.Bytes("foo")
	if want != string(bs) {
		t.Errorf("want: %s, got: %s", want, string(bs))
	}

	bs = c.Arguments.Bytes("bar")
	if bs == nil {
		t.Errorf("error")
	}

	err := ioutil.WriteFile("testdata/bar.gif", bs, 0644)
	if err != nil {
		t.Error(err)
	}

	bs = c.Arguments.Bytes("baz")
	if bs == nil {
		t.Errorf("error")
	}

	err = ioutil.WriteFile("testdata/baz.png", bs, 0644)
	if err != nil {
		t.Error(err)
	}
}
